mapper.select:
select adminId, adminName, sex, tel, email, cardId, role
from "admin"
where adminId = #{username} and pwd = #{password}

select teacherId, teacherName, institute, sex, tel, email, cardId, type, role
from "teacher"
where teacherId = #{username} and pwd = #{password}

select studentId, studentName, grade, major, clazz, institute, tel, email, cardId, sex, role
from "student"
where studentId = #{username} and pwd = #{password}
