mapper.select:
SELECT * FROM "multi_question"
WHERE questionId IN (
    SELECT questionId FROM "paper_manage"
    WHERE questionType = 1 AND paperId = #{paperId}
)

mapper.update:
UPDATE" multi_question"
SET subject = #{subject}, question = #{question}, answerA = #{answerA}, answerB = #{answerB}, answerC = #{answerC}, answerD = #{answerD}, rightAnswer = #{rightAnswer}, analysis = #{analysis}, section = #{section}, level = #{level}
WHERE questionId = #{questionId}
